import axios from 'axios';

const AUTH_API = axios.create({ baseURL: 'http://localhost:4000/api/auth' });

// Hàm đăng nhập (Login)
export const login = async (formData) => {
  try {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };
    const res = await AUTH_API.post('/login', formData, config);
    return res;
  } catch (error) {
    console.error('Login API error:', error.response ? error.response.data : error.message);
    throw error;
  }
};

// Hàm đăng ký (Register)
export const register = async (formData) => {
  try {
    const config = {
      headers: {
        'Content-Type': 'application/json',
      },
    };
    const res = await AUTH_API.post('/register', formData, config);
    return res;
  } catch (error) {
    console.error('Register API error:', error.response ? error.response.data : error.message);
    throw error;
  }
};
